import React from "react";
import css from "./style.module.css";

const BurgerIngredient = (props) => {
  if (props.type == "Salad")
    return <div className={css.$`props.type`}>{props.type}</div>;

  return <div>{props.type}</div>;
};

export default BurgerIngredient;
